	4:// CHOOSING
		IF dfa.bEnterStat THEN
			 // ======== ENTER =========== //
			dfa.endEnter();
			// ======== END ENTER ======== //
		END_IF

		//---------- BODY -----------//
		list.man_cmp_mode_job();
		//------- END_BODY ----------//

		IF dfa.bQuitStat THEN 
		// ========== EXIT =========== //
			IF bManualMode THEN
				list.man_counters_init();
			END_IF		
		// ======== END EXIT ======== //
		END_IF

	5:// ROUGH_LOADING
		IF dfa.bEnterStat THEN
			 // ======== ENTER =========== //
			IF bManualMode THEN 
				IF manWeightLimit = 0 THEN					
						dfa.exec('back'); 
					ELSE
						switch.on( componentNames[ i_man_count ], TRUE );
				END_IF

			END_IF

			dfa.endEnter();
			// ======== END ENTER ======== //
		END_IF

		//---------- BODY -----------//
		IF bPause THEN dfa.exec('pause'); END_IF

		IF bManualMode THEN
			rWeight := rWeight + 0.01;
			list.man_mode_count();
			IF rWeight - rWeight_c_p >= ( manWeightLimit - manWeightPrecise ) THEN
				dfa.exec( 'done' );
			END_IF
		END_IF
		//------- END_BODY ----------//

		IF dfa.bQuitStat THEN 
		// ========== EXIT =========== //
		switch.allOff();

		// ======== END EXIT ======== //
		END_IF

	6:// PRECISE_LOADING
		IF dfa.bEnterStat THEN
			 // ======== ENTER =========== //
			dfa.endEnter();
			// ======== END ENTER ======== //
		END_IF

		//---------- BODY -----------//
		IF bPause THEN dfa.exec('pause'); END_IF

		IF bManualMode THEN
			precisionTimer( IN := TRUE, PT := T#5S );
			IF precisionTimer.Q THEN
				switch.on( componentNames[ i_man_count ], FALSE );
				rWeight := rWeight + 0.005;
				list.man_mode_count();
			END_IF
			IF rWeight - rWeight_c_p >= ( manWeightLimit ) THEN
				dfa.exec( 'listEnd' );
			END_IF
		END_IF
		//------- END_BODY ----------//

		IF dfa.bQuitStat THEN 
		// ========== EXIT =========== //
		precisionTimer( IN := FALSE );
		switch.allOff();

		// ======== END EXIT ======== //
		END_IF

	7:// DONE
		IF dfa.bEnterStat THEN
			 // ======== ENTER =========== //

			dfa.endEnter();
			// ======== END ENTER ======== //
		END_IF		
		//---------- BODY -----------//

		//------- END_BODY ----------//

		IF dfa.bQuitStat THEN 
		// ========== EXIT =========== //


		// ======== END EXIT ======== //
		END_IF

	8:// PAUSE
		IF dfa.bEnterStat THEN
			 // ======== ENTER =========== //
				switch.allOff();
			 	logger.writeStat('Pause mode activated');
			 IF logger.done THEN
				dfa.endEnter();
			 END_IF
			// ======== END ENTER ======== //
		END_IF

		//---------- BODY -----------//
		IF NOT bPause THEN
			dfa.exec('back');			
			//switch.on( switch.sLastCode, switch.bLastRough );
		END_IF

		//------- END_BODY ----------//

		IF dfa.bQuitStat THEN 
		// ========== EXIT =========== //


		// ======== END EXIT ======== //
		END_IF

	ELSE	// состояние по-умолчанию

END_CASE
