IF NOT xFileStdInit THEN
	done :=	FALSE;

	fOpen(xExecute:=FALSE);

	fClose(xExecute:=FALSE);

	fWrite(xExecute:=FALSE);

	fRead(xExecute:=FALSE);

	xFileStdInit:=TRUE;

	uiFileStdState:=0;

ELSE
	CASE uiFileStdState OF

		0: (* open file *)

         	fOpen.sFileName:= filename;

			fOpen.eFileMode:= FILE.MODE.MAPPD;

			fOpen.xExclusive:=TRUE;

			fOpen( xExecute:=TRUE );

				IF fOpen.xDone THEN

					hFile := fOpen.hFile;

					uiFileStdState := 2;

				END_IF

			   	IF fOpen.xError THEN (* error handling*)

					fOpen(xExecute:=FALSE);

					uiFileStdState := 1;

				  	error := 'error file opening, trying to create...' ;

			   	END_IF
				1:(* create file - if not exists *)

				   fOpen.eFileMode:= FILE.MODE.MWRITE;

				   fOpen( xExecute:=TRUE );

				   IF fOpen.xDone THEN

						fOpen( xExecute := FALSE );

						uiFileStdState := 0;

						error := '';

					END_IF

					IF fOpen.xError THEN
						fOpen( xExecute := FALSE );

						uiFileStdState := 4;

						error := 'error file creating, log failed' ;

					END_IF

                2:(* write text in the file *)
					fWrite.hFile := hFile;
					message := Standard.CONCAT ( data, '$r' );
					message := Standard.CONCAT ( Standard.CONCAT ( now(), ' ' ) , message );
					fWrite.pBuffer:=ADR(message);
					szFileSize:= INT_TO_UINT( Standard.LEN( message ) );

					fWrite.szSize := szFileSize;

				   	fWrite.udiTimeOut:=100000;                     (* 100ms Timeout *)

				   	fWrite( xExecute:=TRUE);

					   IF fWrite.xDone THEN

						   SetPos( hFile := hFile, udiPos := 2);

						   uiFileStdState:=3;

					   END_IF

					   IF fWrite.xError THEN

									   (* error handling*)

									   ;

					   END_IF


                3:             (* close file  *)

					   fClose.hFile:= hFile;

					   fClose( xExecute:=TRUE);

					   IF fClose.xDone THEN

									   uiFileStdState:=4;

					   END_IF

					   IF fClose.xError THEN

									   (* error handling*)

									   ;

					   END_IF

				4:   (* end of log *)
						exitLog();
						done  := TRUE;
                END_CASE


END_IF

// Get time in seconds since 1970 :
Date_and_time_in_seconds := SysTimeRtcGet(Date_and_Time_result);
IF Date_and_Time_result <> 0 THEN
RETURN;
END_IF

// Convert UTC seconds to local time seconds, regarding Timezone and Summer time :
Date_and_Time_result := SysTimeRtcConvertUtcToLocal(Date_and_time_in_seconds, Local_date_time_seconds);
IF Date_and_Time_result <> 0 THEN
RETURN;
END_IF

// Convert the UTC seconds to a DATE_AND_TIME variable dt#yyyy-mm-dd-hh:mm:ss :
Date_and_time_format := UDINT_TO_DT(Local_date_time_seconds);

// Convert DATE_AND_TIME to a String :
Date_and_time_string := DT_TO_STRING(Date_and_time_format);

date_Y_M_D := Standard.MID( Date_and_time_string, 10, 4 );

now := REPLACE ( Date_and_time_string, '', 3, 1 );

